from django.conf.urls.defaults import *urlpatterns = patterns('',	#recipes/ -- That is what Thien call it to represent the recipe list	#         -- What goes on the url	#if Thien goes to /cookbook/recipes/, django will go to recipe_list view	(r'^recipes/$', 'cookbook.recipes.views.recipe_list'),	#if Thien goes to /cookbook/recipes/1/, django will go to recipe view 	#(the recipe represent with id '1')	(r'^recipes/(?P<object_id>\d+)/$', 'cookbook.recipes.views.recipe'),)